    private void submitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitButtonActionPerformed

        if (!isQuestionLoaded) {
            message_label.setText("firstly,enter username and click next");
            return;
        }
        String username = username_textfield.getText();
        String userAnswer = answer_textfield.getText().trim();  // Answer entered by user

        // Check if answer is empty
        if (userAnswer.isEmpty()) {
            message_label.setText("enter the answer");
            return;
        }
        try {
            ConnectionClass obj = new ConnectionClass();
            String query = "";
            query = "select security_question,security_answer from admin where username='" + username + "'";

            ResultSet result = obj.stmt.executeQuery(query);
            if (result.next()) {
                String correctAnswer = result.getString("security_answer").trim();
//        System.out.println("correct answer "+correctAnswer);
//        System.out.println("user answer "+userAnswer);
                if (userAnswer.equalsIgnoreCase(correctAnswer)) {
                    message_label.setText("answer verified.");
                    // Load the next screen (e.g., ResetPasswordFrame)

                    setPassword setFrame = new setPassword();
                    setFrame.setVisible(true);
                    this.setVisible(false);// Close current frame

                } else {
                    message_label.setText("Incorrect answer. Try again.");
                }
            } else {
                message_label.setText("User not found.");

            }
        } catch (Exception e) {
            e.printStackTrace();
            System.out.println("database error occured");
        }


    }//GEN-LAST:event_submitButtonActionPerformed

