    private void next_ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_next_ButtonActionPerformed
        // TODO add your handling code here:
        String username = username_textfield.getText();

        String userAnswer = answer_textfield.getText();
        if (username.isEmpty()) {
            message_label.setText("text field cannot be empty.");
            return;
        }

        try {

            ConnectionClass obj = new ConnectionClass();
            String query = "";

            query = "select security_question,security_answer from admin where username='" + username + "'";

            ResultSet result = obj.stmt.executeQuery(query);

            if (result.next()) {
                String question = result.getString("security_question");

                question_label.setText(question);
                isQuestionLoaded = true;
                message_label.setText(" ");
            } else {
                message_label.setText("username not found");
                isQuestionLoaded = false;
            }

        } catch (Exception e) {
            e.printStackTrace();
            message_label.setText("error connecting to database");
        }


    }//GEN-LAST:event_next_ButtonActionPerformed

